import React, { useState } from 'react';

import Header from './Header';

// JSX

function App() {

  const [count, setCount] = useState(0);

  /**
   * useState
   * array = [valor, funcaoDeAtualizacao]
   */

  function added(){
    setCount(count + 1)
    console.log(count);
  }

  return (
    <div>
      <Header>Contador: {count} </Header>
      <button onClick={added}>Adicionar </button>
    </div>
  );
}

export default App;
